import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  // Toggle the menu open or closed
  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <nav style={styles.navbar}>
      <div style={styles.hamburger} onClick={toggleMenu}>
        &#9776; {/* Hamburger icon */}
      </div>
      <ul style={{ ...styles.navList, ...(isOpen ? styles.showMenu : {}) }}>
        <li style={styles.navItem}>
          <Link to="/" style={styles.link}>Home</Link>
        </li>
        <li style={styles.navItem}>
          <Link to="/about" style={styles.link}>About Us</Link>
        </li>
        <li style={styles.navItem}>
          <Link to="/contact" style={styles.link}>Contact</Link>
        </li>
        <li style={styles.navItem}>
          <Link to="/dashboard" style={styles.link}>COVID-Dashboard</Link>
        </li>
      </ul>
    </nav>
  );
};

const styles = {
  navbar: {
    background: "green",
    padding: "15px",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
  hamburger: {
    display: "none",
    fontSize: "24px",
    color: "#fff",
    cursor: "pointer",
  },
  navList: {
    display: "flex",
    listStyle: "none",
    margin: 0,
    padding: 0,
  },
  navItem: {
    marginRight: "20px",
  },
  link: {
    color: "#fff",
    textDecoration: "none",
    fontSize: "20px",
  },
  showMenu: {
    display: "block",
  },

  // Media query for smaller screens
  '@media (max-width: 768px)': {
    hamburger: {
      display: "block", // Show hamburger icon on smaller screens
    },
    navList: {
      display: "none", // Hide menu initially on smaller screens
      position: "absolute",
      top: "50px",
      left: 0,
      right: 0,
      backgroundColor: "green",
      flexDirection: "column",
      alignItems: "center",
      padding: "10px",
    },
    navItem: {
      margin: "10px 0", // Space between items in the dropdown
    },
  },
};

export default Navbar;
